package com.mjpg.usuarios

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.LinearLayout
import android.widget.Toast
import androidx.recyclerview.widget.LinearLayoutManager
import com.mjpg.usuarios.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity(), Eventos{
    private lateinit var binding:ActivityMainBinding;
    private lateinit var linearLayout:LinearLayoutManager;

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater);
        setContentView(binding.root);

        binding.recyclerview.adapter = UserAdapter(getUsuarios(), this);
        linearLayout = LinearLayoutManager(this)
        binding.recyclerview.layoutManager = linearLayout;
        binding.recyclerview.setHasFixedSize(true);
    }

    private fun getUsuarios():MutableList<Usuario>{
        var usuarios = mutableListOf<Usuario>();
        var usuario1 = Usuario(1, "Manolo", "Pérez", "data:image/jpeg;base64,")
        var usuario2 = Usuario(2, "Mario", "Carlo", "data:image/jpeg;base64,")
        var usuario3 = Usuario(3, "Sub", "Normal", "data:image/jpeg;base64,")
        var usuario4 = Usuario(4, "Quiero", "Morir", "data:image/jpeg;base64,")

        usuarios.add(usuario1);
        usuarios.add(usuario2);
        usuarios.add(usuario3);
        usuarios.add(usuario4);
        usuarios.add(usuario1);
        usuarios.add(usuario2);
        usuarios.add(usuario3);
        usuarios.add(usuario4);
        usuarios.add(usuario4);
        usuarios.add(usuario4);
        usuarios.add(usuario4);
        usuarios.add(usuario4);

        return usuarios;
    }

    override fun pulsacionCorta(usuario: Usuario, posicion: Int) {
        Toast.makeText(this, "$posicion ${usuario.giveFullName()}", Toast.LENGTH_LONG).show();
    }

    override fun pulsacionLarga(posicion: Int):Boolean {
        Toast.makeText(this, "Pulsación larga en $posicion ", Toast.LENGTH_LONG).show();
        return true;
    }
}